	CEI_PTEMP_SAME_JOB,
	CEI_AVG_FT_HOURS,
	CEI_AVG_FT_HOUR_WK,
	CEI_AVG_FT_DAY_WK,
	CEI_CLAIM_NBR,
	CEI_EPR_SEQ_NBR,
	CEI_CLIENT_ID
	FROM (
		SELECT 
			CEI_WAGE_RT_AMT,
			CEI_WAGE_BASIS_CD,
			CEI_AVG_WKLY_AMT,
			CEI_OCCUPATION_CD,
			CEI_EMP_ID_NBR,
			CEI_EMP_ID_TYPE_CD,
			CEI_FTEMP_SAME_JOB,
			CEI_PTEMP_SAME_JOB,
			CEI_AVG_FT_HOURS,
			CEI_AVG_FT_HOUR_WK,
			CEI_AVG_FT_DAY_WK,
			CEI_CLAIM_NBR,
			CEI_EPR_SEQ_NBR,
			CEI_CLIENT_ID
		FROM CLAIM_EMPLOYER_STAGE
	)
	QUALIFY ROW_NUMBER() OVER (PARTITION BY CEI_CLAIM_NBR,CEI_EPR_SEQ_NBR,CEI_CLIENT_ID ORDER BY CEI_WAGE_RT_AMT) = 1
